CREATE PROCEDURE [dbo].[usp_Groups_Add] @Name        VARCHAR(100), 
                                        @Description VARCHAR(500)
AS

BEGIN
    UPDATE dbo.[Services]
      SET 
          GroupId = 0
    WHERE GroupId = @Id;
    DELETE FROM dbo.Groups
    WHERE Id = @Id;
END;

CREATE PROCEDURE [dbo].[usp_Groups_Get]
	@Id INT
AS

INSERT INTO [dbo].[Services]
([GroupId], 
 [Name], 
 [Description], 
 [Url], 
 [Params], 
 [ResponseCode], 
 [Enable], 
 [Status]
)
VALUES
(@GroupId, 
 @Name, 
 @Description, 
 @Url, 
 @Params, 
 @ResponseCode, 
 @Enable, 
 @Status
);

BEGIN
    DELETE FROM dbo.[Services]
    WHERE Id = @Id;
END;

BEGIN
    SELECT * FROM dbo.[Services] WHERE Id = @Id
END;

BEGIN        
    SELECT COUNT(*) AS TotalService, SUM(CASE WHEN Status = 0 THEN 1 ELSE 0 END) AS ErrorService
    FROM dbo.[Services]
    WHERE Enable = 1;
END;

BEGIN
    IF (@GroupId > 0)
        SELECT *
        FROM dbo.[Services]
        WHERE GroupId = @GroupId;
    ELSE
        SELECT *
        FROM dbo.[Services];
END;

